using Albstones.WebApp.Helpers;
using Albstones.WebApp.Models;
using Bogus;
using CoordinateSharp;
using Serilog;
using Xunit.Abstractions;

namespace WebApp.Tests
{
    public class AlbstoneTest
    {
        public AlbstoneTest(ITestOutputHelper output)
        {
            Log.Logger = new LoggerConfiguration()
            // add the xunit test output sink to the serilog logger
            // https://github.com/trbenning/serilog-sinks-xunit#serilog-sinks-xunit
            .WriteTo.TestOutput(output)
            .CreateLogger();
        }

        [Fact]
        public void AlbstoneToJson()
        {
            // Arrange
            string expected = File.ReadAllText("Testdata/albstone.json");
            var albstone = new Albstone()
            {
                Address = "Address",
                Name = "Name",
                Date = new DateTime(1972, 01, 09, 08, 00, 00),
                Latitude = 48.3553639,
                Longitude = 8.9680407,
                Message = "Message",
                Image = "Image",
            };

            // Act
            string actual = albstone.ToJson();

            // Assert
            Assert.Equal(expected, actual);
        }

        [Fact]
        public void AlbstoneFormatCoordinate()
        {
            // Arrange
            string expected = @"N 48º 21' 19,31"" E 8º 58' 4,947""";
            var albstone = new Albstone()
            {
                Address = "Address",
                Name = "Name",
                Date = new DateTime(1972, 01, 09, 08, 00, 00),
                Latitude = 48.3553639,
                Longitude = 8.9680407,
                Message = "Message",
                Image = "Image",
            };

            // Act
            string actual = albstone.FormatCoordinate();

            // Assert
            Assert.Equal(expected, actual);
        }

        [Fact]
        public void AlbstoneParse()
        {
            // Arrange
            string json = File.ReadAllText("Testdata/albstone.json");
            var expected = new Albstone()
            {
                Address = "Address",
                Name = "Name",
                Date = new DateTime(1972, 01, 09, 08, 00, 00),
                Latitude = 48.3553639,
                Longitude = 8.9680407,
                Message = "Message",
                Image = "Image",
            };

            // Act
            var actual = Albstone.Parse(json);

            // Assert
            Assert.Equivalent(expected, actual);
        }

        [Fact]
        public void AlbstoneParseWithImage()
        {
            // Arrange
            string json = File.ReadAllText("Testdata/albstone128x128.json");
            string image = "Testdata/albstone128x128.png";
            var expected = new Albstone()
            {
                Address = "Address",
                Name = "Name",
                Date = new DateTime(1972, 01, 09, 08, 00, 00),
                Latitude = 48.3553639,
                Longitude = 8.9680407,
                Message = "Message",
                Image = "",
            };

            // Act
            var actual = Albstone.Parse(json, image);

            // Assert
            Assert.Equivalent(expected, actual);
        }

        [Fact]
        public void AlbstoneFaker()
        {
            // Arrange
            string albstone0 = File.ReadAllText("Testdata/albstone0.json");
            string albstone1 = File.ReadAllText("Testdata/albstone1.json");
            string albstone2 = File.ReadAllText("Testdata/albstone2.json");

            Randomizer.Seed = new Random(420);
            DateTime millenium = new DateTime(2000, 1, 1, 0, 0, 0);

            var albstoneFaker = new Faker<Albstone>("de")
                .RuleFor(a => a.Name, f => f.Name.FirstName(f.Person.Gender))
                .RuleFor(a => a.Date, f => f.Date.Future(20, millenium))
                .RuleFor(a => a.Latitude, f => f.Address.Latitude())
                .RuleFor(a => a.Longitude, f => f.Address.Longitude())
                .RuleFor(a => a.Message, f => f.Hacker.Phrase())
                .RuleFor(a => a.Image, "");

            var albstones = albstoneFaker.Generate(3);

            foreach (var albstone in albstones)
            {
                Coordinate coordinate = new Coordinate(albstone.Latitude, albstone.Longitude, albstone.Date);
                string[] mnemonic = Magic.Mnemonic(albstone.Name, coordinate);
                string seed = Magic.SeedHex(mnemonic);
                albstone.Address = Magic.Address(seed, 0);
            }

            // Act
            foreach (var albstone in albstones)
            {
                Log.Information("{albstone}", albstone.ToJson());
            }

            // Assert
            Assert.Equal(albstone0, albstones[0].ToJson());
            Assert.Equal(albstone1, albstones[1].ToJson());
            Assert.Equal(albstone2, albstones[2].ToJson());
        }
    }
}
